import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import './App.css';

const App = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const messagesEndRef = useRef(null);

  // Scroll to bottom of chat
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // Handle API call to OpenRouter
  const sendMessage = async () => {
    if (!input.trim()) return;

    const userMessage = { role: 'user', content: input };
    setMessages([...messages, userMessage]);
    setInput('');
    setLoading(true);

    try {
      const response = await axios.post(
        'https://openrouter.ai/api/v1/chat/completions',
        {
          model: '',
          messages: [...messages, userMessage],
        },
        {
          headers: {
            'Authorization': `Bearer ${process.env.REACT_APP_API_KEY}`, // Use environment variable
            'HTTP-Referer': window.location.href,
            'X-Title': 'VS Code Chatbot',
          },
        }
      );

      setMessages([...messages, userMessage, response.data.choices[0].message]);
    } catch (error) {
      console.error('Error:', error);
      setMessages([...messages, userMessage, { role: 'assistant', content: 'ðŸš¨ Error: API call failed!' }]);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    sendMessage();
  };

  return (
    <div className="app">
      <h1>VS Code Chatbot ðŸ¤–</h1>
      <div className="chat-window">
        {messages.map((msg, i) => (
          <div key={i} className={`message ${msg.role}`}>
            <strong>{msg.role === 'user' ? 'You' : 'AI'}:</strong> {msg.content}
          </div>
        ))}
        {loading && <div className="message assistant">ðŸ’­ Thinking...</div>}
        <div ref={messagesEndRef} />
      </div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type here..."
          disabled={loading}
        />
        <button type="submit" disabled={loading}>
          {loading ? 'âŒ›' : 'ðŸš€ Send'}
        </button>
      </form>
    </div>
  );
};

export default App;
